import React from "react";
import "@blueprintjs/datetime/lib/css/blueprint-datetime.css";
import "@blueprintjs/core/lib/css/blueprint.css";
import { DateInput } from "@blueprintjs/datetime";
import { Button } from "@blueprintjs/core";

import { Classes, Popover2 } from "@blueprintjs/popover2";

function RentalDatePicker({
  type,
  setPickupDate,
  setDropoffDate,
  pickupDate,
  dropoffDate,
}) {
  return (
    <div>
      {/* <Popover2
        interactionKind="click"
        popoverClassName={Classes.POPOVER2_CONTENT_SIZING}
        placement="bottom"
        content={
          <DatePicker
            highlightCurrentDay={true}
            onChange={(selectedDate) => {
              if (type === "Pick-Up") setPickupDate(selectedDate);
              if (type === "Drop-Off") setDropoffDate(selectedDate);
            }}
          />
        }
        renderTarget={({ isOpen, ref, ...targetProps }) => (
          <Button
            {...targetProps}
            elementRef={ref}
            intent="primary"
            text={`Choose a ${type} date`}
          />
        )}
      /> */}
      {type === "Pick-Up" && (
        <DateInput
          formatDate={(date) => date.toLocaleString()}
          onChange={(selectedDate) => {
            if (type === "Pick-Up") setPickupDate(selectedDate);
          }}
          parseDate={(str) => new Date(str)}
          placeholder={"M/D/YYYY"}
          highlightCurrentDay={true}
          value={!!pickupDate ? pickupDate : Date.now()}
        />
      )}

      {type === "Drop-Off" && (
        <DateInput
          formatDate={(date) => date.toLocaleString()}
          onChange={(selectedDate) => {
            if (type === "Drop-Off") setDropoffDate(selectedDate);
          }}
          parseDate={(str) => new Date(str)}
          placeholder={"M/D/YYYY"}
          highlightCurrentDay={true}
          value={!!dropoffDate ? dropoffDate : Date.now()}
        />
      )}
    </div>
  );
}

export default RentalDatePicker;
