import React from "react";
import { HTMLSelect } from "@blueprintjs/core";

const locations = [
  { name: "JFK", id: 1 },
  { name: "LGA", id: 2 },
  { name: "DFW", id: 3 },
];

function LocationPicker({ type, setPickupLocation, setDropoffLocation }) {
  return (
    <HTMLSelect
      large={true}
      onChange={(val) => {
        if (type === "Pick-Up") setPickupLocation(val);
        if (type === "Drop-Off") setDropoffLocation(val);
      }}
    >
      <option selected>{`Choose ${type} location`}</option>
      {locations.map((location) => {
        return <option value={location.name}>{location.name}</option>;
      })}
    </HTMLSelect>
  );
}

export default LocationPicker;
