import React, { useState } from "react";
import "../styles/DigitalMesh.css";
import {
  Card,
  InputGroup,
  Button,
  MenuItem,
  Popover,
  Menu,
} from "@blueprintjs/core";
import { DatePicker, TimePicker } from "@blueprintjs/datetime";
import { Select } from "@blueprintjs/select";

const locations = [
  { name: "JFK", id: 1 },
  { name: "LGA", id: 2 },
  { name: "DFW", id: 3 },
];

const SubmitForm = () => {
  const [selectedLocation, setSelectedLocation] = useState(locations[0]);
  const handleClick = () => {
    console.log("clicked");
  };

  const renderLocation = (location, { handleClick, modifiers, query }) => {
    if (!modifiers.matchesPredicate) {
      return null;
    }
    const text = `${location.name}`;
    return (
      <MenuItem
        active={modifiers.active}
        disabled={modifiers.disabled}
        key={location.id}
        onClick={handleClick}
        text={text}
      />
    );
  };
  return (
    <div>
      <div className="card-container">
        <Card className="car-card" interactive={true}>
          <h5>
            <a href="#">IBM_1</a>
          </h5>
          <p>Card content</p>
          <div className="drop-down-menu">
            <Select
              items={locations}
              itemPredicate={() => {}}
              itemRenderer={renderLocation}
              noResults={<MenuItem disabled={true} text="No results." />}
              onItemSelect={setSelectedLocation}
            >
              <Button text={locations[0].name} rightIcon="caret-down" />
            </Select>

            <InputGroup type="search" placeholder="Pick Up Location" />
            <InputGroup type="search" placeholder="Pick Up Date" />
            <InputGroup type="search" placeholder="Pick Up Time" />
            <InputGroup type="search" placeholder="Drop Off Location" />
            <InputGroup type="search" placeholder="Drop Off Date" />
            <InputGroup type="search" placeholder="Drop Off Time" />
          </div>
          <Button>Submit</Button>
        </Card>
      </div>
    </div>
  );
};

export default SubmitForm;
